#Combien y a-t-il de handle dans cette array ?
def nb_handle(journalists)
    puts journalists.length
end

#Quel est le handle le plus court de cette liste ?
def min_handle(journalists)
    puts journalists.min{|a,b| a.size <=> b.size}
end

#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def handle_5(journalists)
    puts journalists.count{|a| a.length == 6}
end

#Combien commencent par une majuscule (première lettre juste après le @) ?
def up_handle(journalists)
    puts journalists.count {|e| e =~ /^@[A-Z]/}
end


#Trie la liste de handle par ordre alphabétique.
def tri_handle(journalists)
    puts journalists.sort_by {|a| a.downcase}
end


#Trie la liste de handle par taille des handle 
def size_handle(journalists)
 puts journalists.sort{|a, b| a.length <=> b.length}
end

 #Quelle est la position dans l'array de la personne @epenser ?
 def pos_handle(journalists)
    puts journalists.find_index("@epenser")
 end

 #Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

 def size_count(journalists)
    i = 0
    journalists.max{|a, b| a.length <=> b.length} #combien de caractere max il y a (33)
    while (i <= 33)
    print "nombre d'handle avec #{i} caractere : "
    puts journalists.count{|a| a.length == i}
    i += 1
    end
 end


